import React, { useRef, useState } from 'react'

function UseR() {
    const ElementItem = useRef("");
    const [name , setName] = useState("Neelesh");
    function update(){
        setName("")
        ElementItem.current.focus()
    
    }
    function item(){
        ElementItem.current.style.color = "blue";
        ElementItem.current.value = "prajapati";
    }
  return (
    <div>
      <h1>learning useRef</h1>
      <input type='text' ref={ElementItem} value={name} onChange={(e)=>setName(e.target.value)}></input>
      <button onClick={update}>Reset</button>
      <button onClick={item}>Add Color</button>

 

    </div>
  )
}

export default UseR
